<?php

namespace App\Http\Controllers;

use App\Models\FormulirAplikasiRpl;
use App\Models\Peserta;
use Illuminate\Http\Request;
use App\Models\RiwayatHidupForm;
use App\Models\PaktaIntegritasRpl;
use Illuminate\Support\Facades\Auth;


class DownloadFileController extends Controller
{
    public $logo = 'data:image/png;base64,';
    public function downloadPaktaIntegritas()
    {
        $logo = $this->logo;
        return view('files.pakta-integritas', [
            'paktaIntegritas'   => PaktaIntegritasRpl::where('user_id', auth()->user()->id)->first(),
            'logo' => $logo
        ]);
    }

    public function downloadForm2()
    {
        return view('files.form-2', [
            'peserta'   => Peserta::where('no_peserta', Auth::user()->no_peserta)->with(['formulirAplikasiRpl'])->first()
        ]);
    }
    public function downloadForm3()
    {
        return view('files.form-3', [
            'peserta'   => Peserta::where('no_peserta', Auth::user()->no_peserta)->with(['formulirAplikasiRpl'])->first(),
            'formulirAplikasiRpl' => FormulirAplikasiRpl::where('no_peserta', Auth::user()->no_peserta)->get()
        ]);
    }
    public function downloadForm7()
    {
        $riwayatHidupForms = RiwayatHidupForm::with(['fields', 'fields.riwayatHidup' => function ($query) {
            $query->where('no_peserta', Auth::user()->no_peserta);
        }])->where('is_active', 1)->get();
        $peserta = Peserta::where('no_peserta', Auth::user()->no_peserta)->first();
        return view('files.form-7', compact('riwayatHidupForms', 'peserta'));
    }

    public function beritaAcara($no_peserta)
    {
        $logo = $this->logo;
        $peserta = Peserta::where('no_peserta', $no_peserta)->first();
        return view('files.berita-acara', compact('peserta', 'logo'));

    }

}


